class RuleLoader:
    def __init__(self):
        self.driver = GraphDatabase.driver(NEO4J_URI, auth=(NEO4J_USER, NEO4J_PASSWORD))

    def load(self):
        with self.driver.session(database="BrainDB") as session:
            # Û±. Ø­Ø°Ù Ù‚ÙˆØ§Ù†ÛŒÙ† Ù‚Ø¨Ù„ÛŒ (Ø¨Ø±Ø§ÛŒ Ø¬Ù„ÙˆÚ¯ÛŒØ±ÛŒ Ø§Ø² Ø¯ÙˆÚ¯Ø§Ù†Ú¯ÛŒ)
            print("ðŸ§¹ Ø¯Ø± Ø­Ø§Ù„ Ù¾Ø§Ú©Ø³Ø§Ø²ÛŒ Ù‚ÙˆØ§Ù†ÛŒÙ† Ù‚Ø¯ÛŒÙ…ÛŒ...")
            session.run("MATCH (r:Rule) DETACH DELETE r")
            
            # Û². Ø§ØªØµØ§Ù„ Ù‚ÙˆØ§Ù†ÛŒÙ† Ø¹Ù…ÙˆÙ…ÛŒ Ø¨Ù‡ ÙØµÙ„
            print(f"--- Ø¨Ø§Ø±Ú¯Ø°Ø§Ø±ÛŒ {len(general_rules)} Ù‚Ø§Ù†ÙˆÙ† Ø¹Ù…ÙˆÙ…ÛŒ ---")
            for rule in general_rules:
                session.run("""
                    MATCH (c:Chapter {chapter_id: '01'})
                    MERGE (r:Rule {rule_id: $rid})
                    SET r.raw_text = $text, r.scope = 'General'
                    MERGE (c)-[:HAS_RULE]->(r)
                """, rid=rule['rule_id'], text=rule['text'])
            
            # Û³. Ø§ØªØµØ§Ù„ Ù‚ÙˆØ§Ù†ÛŒÙ† Ú¯Ø±ÙˆÙ‡ÛŒ Ø¨Ù‡ Ú¯Ø±ÙˆÙ‡â€ŒÙ‡Ø§ (Ø§Ø±Ø«â€ŒØ¨Ø±ÛŒ)
            print(f"--- Ø¨Ø§Ø±Ú¯Ø°Ø§Ø±ÛŒ {len(group_rules)} Ù‚Ø§Ù†ÙˆÙ† Ú¯Ø±ÙˆÙ‡ÛŒ ---")
            for rule in group_rules:
                session.run("""
                    MATCH (g:Group {group_id: $gid})
                    MERGE (r:Rule {rule_id: $rid})
                    SET r.raw_text = $text, r.scope = 'Group'
                    MERGE (g)-[:HAS_RULE]->(r)
                """, gid=rule['group_id'], rid=rule['rule_id'], text=rule['text'])
                
            print("âœ… ØªÙ…Ø§Ù… Û³Û¶ Ù‚Ø§Ù†ÙˆÙ† Ø¨Ø§ Ø³Ø§Ø®ØªØ§Ø± Ø¬Ø¯ÛŒØ¯ Ùˆ ØµØ­ÛŒØ­ Ø¨Ø§Ø±Ú¯Ø°Ø§Ø±ÛŒ Ø´Ø¯Ù†Ø¯.")

    def close(self):
        self.driver.close()

if __name__ == "__main__":
    loader = RuleLoader()
    loader.load()
    loader.close()